function extract(h,m,s,type) {
if (!document.images) return;
if (h <= 9) {
document.images.a.src = cb.src;
document.images.a.width = 1;
document.images.b.src = eval("c"+h+".src");
}
else {
document.images.a.width = document.images.b.width;
document.images.a.src = eval("c"+Math.floor(h/10)+".src");
document.images.b.src = eval("c"+(h%10)+".src");
}
if (m <= 9) {
document.images.d.src = c0.src;
document.images.e.src = eval("c"+m+".src");
}
else {
document.images.d.src = eval("c"+Math.floor(m/10)+".src");
document.images.e.src = eval("c"+(m%10)+".src");
}
if (s <= 9) {
document.g.src = c0.src;
document.images.h.src = eval("c"+s+".src");
}
else {
document.images.g.src = eval("c"+Math.floor(s/10)+".src");
document.images.h.src = eval("c"+(s%10)+".src");
}
if (dn == "AM") document.j.src = cam.src;
else document.images.j.src = cpm.src;
}
function show3() {
if (!document.images)
return;
var Digital = new Date();
var hours = Digital.getHours();
var minutes = Digital.getMinutes();
var seconds = Digital.getSeconds();
dn = "AM";

var alarmTime = "" + document.thisForm.alarm_hour.value + ":" + document.thisForm.alarm_minute.value + "";

if (hours + ":" + minutes == "" + alarmTime) {
	//window.location.href= "//onlineclock.net/noisegenerator/alarm.html";
	return playAlarm();
}
//found this solution to the AM/PM bug (see main clock - Wrong AM/PM shown for first minute after twelve) here:
////www.web-source.net/web_development/javascript_date.htm

if (hours   > 11) { dn = "PM"; }
if (hours   > 12) { hours = hours - 12; }
if (hours   == 0) { hours = 12; }

extract(hours, minutes, seconds, dn);
setTimeout("show3()", 1000);
}
