function changeBackground(color)
{
    document.bgColor=color;
    //if the background color is not black, add the texture background image to the body element:
    if( color == "black" || color == "#000000" )
    {
        document.body.style.backgroundImage = "none";
    }
    else
    {
        document.body.style.backgroundImage = "url('//onlineclock.net/images/textures/fabric.png')";
    }
}
function removeUnderline(linknumber)
{
var linkArray = [];
linkArray[0] = "lsmall";
linkArray[1] = "lmedium";
linkArray[2] = "llarge";
linkArray[3] = "lxlarge";

	for (i = 0; i <4; i++)
	{
		if(i==linknumber)
		{
			document.getElementById(linkArray[i]).style.textDecoration = "none";
			document.getElementById(linkArray[i]).style.fontWeight = "normal";
		}
		else
		{
			document.getElementById(linkArray[i]).style.textDecoration = "underline";
			document.getElementById(linkArray[i]).style.fontWeight = "bold";
		}
	}
}
